import React from 'react';
import axios from 'axios'
import SearchBar from './components/SearchBar'



class App extends React.Component {
  onSearchSubmit(term){
    //create axios func to call the images first argument => api URL second argu => object get from https://unsplash.com/documentation#location
    axios.get('https://api.unsplash.com/search/photos',{
      // from https://unsplash.com/documentation#search-photos
      params: {query: term},
      //1-object header 
      headers:{
        //from https://unsplash.com/documentation#public-authentication || for access key => https://unsplash.com/oauth/applications/370516
        Authorization: 'Client-ID xKRVMsxoREwR4OtFiqMBlLH9DsQhx59G7EnT1ssBU40'
      }

    }).then (response => {
      console.log(response.data.r);
    })
  }

  render() {
    return(
      <div className="ui container" style={{marginTop:'15px'}}>
        <SearchBar onSubmit={this.onSearchSubmit}/>
      </div>
    )

}
}




export default App;
