import React from 'react';
import './App.css';
import Season from './components/Season'
import Spinner from './components/Spinner'
import TimeComp from './'

class App extends React.Component {
  //constructor func only job is update the state object.
  // constructor(props) {
  //   super(props);
  //   this.state = { lat: null, errMassage: ''}
  // }

  //This line is equivalent the constructor function so no need to use it anymore.
  state = { lat: null, errMassage: ''}

    //Use componentDidMount func to get data from Api
    componentDidMount() {
      window.navigator.geolocation.getCurrentPosition(
        position =>this.setState({ lat: position.coords.latitude}),
        err =>this.setState({ errMassage: err.message})

      )
    }
  //contain all content in one div with one class name using render function
  renderBody() {
    if (this.state.errMassage && !this.state.lat){
      return ( <div>Error: {this.state.errMassage}</div>)
    }
    if (!this.state.errMassage && this.state.lat){
      return (<Season lat={this.state.lat}/>
      )
    }

return (
  <Spinner massage={'Please Accept Location Request'}/>
  )
  }


  render() {
    return(
      <div className='border-red'>
      {this.renderBody()}
      <TimeComp/>
      </div>
    )

}
}




export default App;
