import React from "react";
import {Field , reduxForm } from 'redux-form';


class StreamCreate extends React.Component {
    renderInput({input , label, meta}) {
        return (
        <div className="field">
            <label>{label}</label>
            <input {...input}/>
            <div>{meta.error}</div>
        </div>
        )
    }
    onSubmit = (formValue) => {
        console.log(formValue);
    }

    render() {
        console.log(this.props)
        return (
            <form onSubmit={this.props.handleSubmit(this.onSubmit)} className="ui form"> 
                <Field name='title' component={this.renderInput} label='Enter Title'/>
                <Field name='description' component={this.renderInput} label='Enter Description'/>
                <button className="ui inverted purple button000">Submit</button>
            </form>
        )
    }

}

const validation = (formValue) => {
    const error = {};

    if (!formValue.title) {
        error.title = 'You must provide a title'
    }
    if (!formValue.description) {
        error.description = 'You must provide a description'
    }
    return error;
}

//reduxForm() will return the function which created by streamCreate
export default reduxForm({form: 'streamCreate'})(StreamCreate);
