import jsonPlaceholder from '../api/jsonPlaceholder'

//in this case with redux we can't use async await syntax becua action creator need to return js plain object but async will return a request object

export const fetchPosts =  () =>{
    const promises =  jsonPlaceholder.get('./posts')
    return {
        type: 'FETCH_POSTS',
        payload: promises   
    }
}
