import React from 'react'
import {connect} from 'react-redux'
import {signIn, signOut} from '../actions'

class GoogleAuth extends React.Component {
    state = {isSignedIn: null};

    componentDidMount() {
        window.gapi.load('client:auth2', ()=>{
            window.gapi.client.init({
                clientId:'797401886567-9cumct9mrt3v2va409rasa7fa6fq02hh.apps.googleusercontent.com',
                scope:'email',
                //plugin_name: "streamy"
            }).then(()=>{
                this.auth = window.gapi.auth2.getAuthInstance();
                this.onAuthStateChange();
                this.auth.isSignedIn.listen(this.onAuthStateChange)
            });
        });
    };

    onAuthStateChange = (is) =>{
        this.setState({ isSignedIn: this.auth.isSignedIn.get() });
    };

    onSignInClick = () =>{
        this.auth.signIn();
    };

    onSignOutClick = () =>{
        this.auth.signOut();
    };

    renderAuthButton() {
        if (this.state.isSignedIn === null){
            return null;
        }else if (this.state.isSignedIn){
            return (
                <button onClick={this.onSignOutClick} className='ui red google button'>
                    <i className='google icon'/>
                    Sign Out
                </button>
            )
        }else{
            return (
                <button onClick={this.onSignInClick} className='ui red google button'>
                    <i className='google icon'/>
                    Sign In with Google
                </button>
            )
        }
    };


    render() {
        return (
            <div>
                {this.renderAuthButton()}
            </div>
        )
    }
}

export default connect(null, {signOut, signIn})(GoogleAuth);
