import jsonPlaceholder from '../api/jsonPlaceholder'

//in this case with redux we can't use async await syntax because action creator need to return js plain object but async will return a request object so we need to use a MIDDLEWARE like thunk with redux 

export const fetchPosts =  () =>{
    return function (dispatch, get) {
        const promises =  jsonPlaceholder.get('./posts')
        return {
            type: 'FETCH_POSTS',
            payload: promises   
        }
    }
    }
