import streams from '../apis/streams'
import {SIGN_IN,
        SIGN_OUT,
        CREATE_STREAM,
        DELETE_STREAM,
        FETCH_STREAMS,
        FETCH_STREAM,
        UPDATE_STREAM
    } from '../actions/types'

export const signIn = (userId) =>{
    return {
        type:SIGN_IN,
        payload:userId
    }
}

export const signOut = () =>{
    return {
        type:SIGN_OUT
    }
}


//axios func to create stream store
export const createStream = formValue => async dispatch =>{
    const response = await streams.post ('/streams', formValue)

    dispatch({type: CREATE_STREAM, payload: response.data})
}
//FETCH_STREAMS
export const fetchStreams = formValue => async dispatch =>{
    const response = await streams.post ('/streams', formValue)

    dispatch({type: FETCH_STREAMS, payload: response.data})
}
//FETCH_STREAM
export const fetchtream = (id) => async dispatch =>{
    const response = await streams.post (`/streams/${id}`, formValue)

    dispatch({type: FETCH_STREAM, payload: response.data})
}
