import React from "react";
import {connect} from 'react-redux';
import {createStream} from '../../actions/index'
import StreamForm from './StreamForm'

class StreamCreate extends React.Component {

       onSubmit = (formValue) => {
        this.props.createStream(formValue)
        // console.log(formValue);

    }

    render() {
        console.log(this.props)
        return (
            <form onSubmit={this.props.handleSubmit(this.onSubmit)} className="ui form error"> 
                <Field name='title' component={this.renderInput} label='Enter Title'/>
                <Field name='description' component={this.renderInput} label='Enter Description'/>
                <button className="ui inverted purple button">Submit</button>
            </form>
        )
    }

}

const validation = (formValue) => {
    const errors = {};

    if (!formValue.title) {
        errors.title = 'You must provide a title'
    }
    if (!formValue.description) {
        errors.description = 'You must provide a description'
    }
    return errors;
}

const formWrapped = reduxForm({form: 'streamCreate', validate: validation})(StreamCreate);
//reduxForm() will return the function which created by streamCreate
export default connect(null, {createStream})(formWrapped);
