import React from 'react';


const getSeason = (lat, month) => {
    if (month > 2 && month < 9){
        lat > 0 ? 'Summer' : 'Winter';
    }
    else{
        lat > 0 ? 'Winter' : 'Summer'
    }
}


const Season = props => {
    const season = getSeason(props.lat, new Date().getMonth())
    console.log(season)
    const seasonRender = season === 'winter' ? 'Burr, it is chilly' : 'Lets hit the beach'
    return (
    <div>
        {seasonRender}
    </div>
    )
}

export default Season;
