import _ from 'lodash';
import {
    FETCH_STREAM,
    FETCH_STREAMS,
    DELETE_STREAM,
    UPDATE_STREAM,
    CREATE_STREAM,
} from '../actions/types'


export default (state= {}, action)=>{
    switch(action.type){
        case FETCH_STREAMS:
            return {...state, ..._.mapKeys(action.payload, 'id')}
        case FETCH_STREAM:
            return {...state,[action.payload.id]:action.payload}
        case CREATE_STREAM:
            return {...state,[action.payload.id]:action.payload}
        case UPDATE_STREAM:
            return {...state,[action.payload.id]:action.payload}
        case DELETE_STREAM:
            return _.omit(state, action.payload)
        default:
            return state
    }
}
