import React, { Component} from 'react';


class TimeComp extends Component{
    state = {time: ''}

    componentDidMount (){
        setInterval(() => {
            this.setState({time: new Date().toLocaleTimeString()},
            );
        })
    }
    render() {
        return(
            <div>Time is : {this.state.time}</div>
        )
    }

}

export default TimeComp;
